import React from "react";
import { Card, Carousel } from "antd";

export function IntroCard(props) {
  return (
    <div className="cardBox">
      <div className="iconBox">
        <img src={props.icon} className="icon" />
      </div>
      <div className="cardTitle">{props.title}</div>
      <div className="cardIntro">{props.intro}</div>
    </div>
  );
}

export function CategoryCard(props) {
  return (
    <div
      className="cardBox2 col-lg-3 col-sm-5 m-2"
      style={{
        backgroundImage:
          "linear-gradient(rgba(0, 0, 0, 0.426), rgba(0, 0, 0, 0.426)),url(" +
          props.bg +
          ")",
      }}
    >
      <div style={{ marginTop: "auto" }}>
        <div className="cardTitle2">{props.title}</div>
        <div className="cardIntro2">{props.para}</div>
        <div
          className="btn mt-1"
          onClick={() => console.log("Show me clicked")}
        >
          Shop Now
        </div>
      </div>
    </div>
  );
}

export function ItemCard(props) {
  return (
    <div className="col-lg-3 col-md-4 mb-5">
      <Card
        className="itemCard cardEx"
        cover={
          <>
            <Carousel autoplay>
              <div>
                <h3
                  style={{
                    height: "300px",
                    background: `url(${props.img})`,
                    backgroundPosition: "center",
                    backgroundSize: "cover",
                    backgroundRepeat: "no-repeat",
                  }}
                >
                  <div
                    className="hide"
                    style={{
                      display: "flex",
                      height: "100%",
                      padding: "0.2em",
                      alignItems: "flex-end",
                    }}
                  >
                    <img
                      src="https://img.icons8.com/bubbles/40/fa314a/buy.png"
                      className="logo1"
                      style={{ borderRadius: "50%" }}
                      onClick={() =>
                        props.callback(
                          props.id,
                          props.img,
                          props.title,
                          props.price
                        )
                      }
                    />
                    <div style={{ flex: 1 }} />
                    <img
                      src="https://img.icons8.com/office/40/000000/like--v2.png"
                      className="logo1"
                      style={{ borderRadius: "50%" }}
                      onClick={() =>
                        props.callback(
                          props.id,
                          props.img,
                          props.title,
                          props.price
                        )
                      }
                    />
                  </div>
                </h3>
              </div>
              <div>
                <h3
                  style={{
                    height: "300px",
                    background: `url(${props.img})`,
                    backgroundPosition: "center",
                    backgroundSize: "cover",
                    backgroundRepeat: "no-repeat",
                  }}
                >
                  <div
                    className="hide"
                    style={{
                      display: "flex",
                      height: "100%",
                      padding: "0.2em",
                      alignItems: "flex-end",
                    }}
                  >
                    <img
                      src="https://img.icons8.com/bubbles/40/fa314a/buy.png"
                      className="logo1"
                      style={{ borderRadius: "50%" }}
                      onClick={() =>
                        props.callback(
                          props.id,
                          props.img,
                          props.title,
                          props.price
                        )
                      }
                    />
                    <div style={{ flex: 1 }} />
                    <img
                      src="https://img.icons8.com/office/40/000000/like--v2.png"
                      className="logo1"
                      style={{ borderRadius: "50%" }}
                      onClick={() =>
                        props.callback(
                          props.id,
                          props.img,
                          props.title,
                          props.price
                        )
                      }
                    />
                  </div>
                </h3>
              </div>
            </Carousel>
          </>
        }
      >
        <div style={{ marginTop: "-1.5em" }}>
          <div className="itemTitle">{props.title}</div>
          <div className="itemPrice ">â‚¹{props.price}</div>
          <div className="hide">
            <div style={{ textAlign: "left" }}>
              <h6>
                <span>size:</span> <span className="sizes">34</span>{" "}
                <span className="sizes">36</span>{" "}
                <span className="sizes">38</span>{" "}
              </h6>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}
