const { Header, Content, Footer, Sider } = Layout;

function Products() {
  const [collapsed, setCollapsed] = useState(false);

  return (
    <Router>
      <div
        style={{
          display: "flex",
          width: "100vw",
          height: "100vh",
          marginTop: "4.5em",
        }}
      >
        <Layout>
          <Sider
            collapsible
            collapsed={collapsed}
            onCollapse={() =>
              collapsed ? setCollapsed(false) : setCollapsed(true)
            }
            style={{
              overflow: "auto",
              height: "100vh",
              position: "fixed",
              left: 0,
            }}
          >
            <div className="sidebarTitle">Mens Fashion</div>
            <Menu
              theme="dark"
              mode="inline"
              defaultSelectedKeys={["1"]}
              className="mt-5"
            >
              <Menu.Item
                key="1"
                icon={
                  <img src="https://img.icons8.com/emoji/32/000000/jeans-emoji.png" />
                }
                className="text-warning"
              >
                Jeans
              </Menu.Item>
              <Menu.Item
                key="2"
                icon={
                  <img src="https://img.icons8.com/bubbles/32/000000/t-shirt.png" />
                }
                className="text-warning"
              >
                T-Shirts
                <Link to="/" />
              </Menu.Item>
              <Menu.Item
                key="3"
                icon={
                  <img src="https://img.icons8.com/flat-round/32/000000/short-sleeve-shirt.png" />
                }
                className="text-warning"
              >
                Shirts"
                <Link to="/products" />
              </Menu.Item>
              <Menu.Item
                key="4"
                icon={
                  <img src="https://img.icons8.com/plasticine/32/000000/trousers.png" />
                }
                className="text-warning"
              >
                Trousers
              </Menu.Item>
              <Menu.Item
                key="5"
                icon={
                  <img src="https://img.icons8.com/flat-round/32/000000/sneakers.png" />
                }
                className="text-warning"
              >
                Shoes
              </Menu.Item>
              <Menu.Item
                key="6"
                icon={
                  <img src="https://img.icons8.com/bubbles/32/000000/flip-flops.png" />
                }
                className="text-warning"
              >
                Slippers
              </Menu.Item>
            </Menu>
          </Sider>
          <Layout className="site-layout" style={{ marginLeft: 200 }}>
            <Header
              className="site-layout-background header"
              style={{ backgroundColor: " #001529", zIndex: "11", padding: 0 }}
            >
              <h3 className="headerTitle">Products</h3>
            </Header>
            <Content>
              <JeansItem />
              {/* <div
                className="site-layout-background col-lg-12 p-4"
                style={{
                  minHeight: "100%",
                  minWidth: "100%",
                  flexDirection: "row",
                  backgroundColor: "lightgrey",
                }}
              >
                <Row gutter={10}>
                  <ItemCard
                    img={imgArray[0]}
                    title="Denim Brand Jeans"
                    price="2699"
                  />

                  <ItemCard
                    img={imgArray[1]}
                    title="Urban Classic Slim Jean"
                    price="2199"
                  />

                  <ItemCard
                    img={imgArray[2]}
                    title="Slim Fit Denim Jean"
                    price="2399"
                  />

                  <ItemCard
                    img={imgArray[3]}
                    title="Low Rise Skinny Fit Jean"
                    price="2699"
                  />
                </Row>
              </div> */}
            </Content>
            <Footer style={{ textAlign: "center" }}>
              Ant Design Â©2018 Created by Ant UED
            </Footer>
          </Layout>
        </Layout>
      </div>
    </Router>
  );
}

